	package BuketsSort;

	import java.util.Arrays;

	/**
	 * 
	 * 
	 * @author sxx.xu
	 *
	 */
	public class BuketsSort {

		/*
		 * 
		 *
		 *  a --  max -- a
		 */
		public static void bucketSort(int[] a, int max) {
			int[] buckets;

			if (a == null || max < 1)
				return;

			// maxbucketsbuckets0
			buckets = new int[max];

			// 1. 
			for (int i = 0; i < a.length; i++)
				buckets[a[i]]++;

			// 2. 
			for (int i = 0, j = 0; i < max; i++) {
				while ((buckets[i]--) > 0) {
					a[j++] = i;
				}
			}

			buckets = null;
		}

		public static void bucketSort(int[] arr) {
			// f(x)=x/10
			// 0~99
			int bucketCount = 10;
			Integer[][] bucket = new Integer[bucketCount][arr.length]; // Integernull,0
			for (int i = 0; i < arr.length; i++) {
				int quotient = arr[i] / 10; // f(x)
				for (int j = 0; j < arr.length; j++) {
					if (bucket[quotient][j] == null) {
						bucket[quotient][j] = arr[i];
						break;
					}
				}
			}
			// 
			for (int i = 0; i < bucket.length; i++) {
				// insertion sort
				for (int j = 1; j < bucket[i].length; ++j) {
					if (bucket[i][j] == null) {
						break;
					}
					int value = bucket[i][j];
					int position = j;
					while (position > 0 && bucket[i][position - 1] > value) {
						bucket[i][position] = bucket[i][position - 1];
						position--;
					}
					bucket[i][position] = value;
				}

			}
			// 
			for (int i = 0, index = 0; i < bucket.length; i++) {
				for (int j = 0; j < bucket[i].length; j++) {
					if (bucket[i][j] != null) {
						arr[index] = bucket[i][j];
						index++;
					} else {
						break;
					}
				}
			}
		}

	 public static void bucketSort2(int [] arr){  
	        //minmax  
	        int min = 0, max = 0;  
	        for(int i = 0; i <= arr.length - 1; i++){  
	            if(arr[i] < min){  
	                min = arr[i];  
	            }  
	            if(arr[i] > max){  
	                max = arr[i];  
	            }  
	        }  
	        //max - min + 1  
	        int bucketCount = max - min + 1;  
	        int[] bucket = new int[bucketCount];  
	        for(int i = 0; i <= arr.length - 1; i++){  
	            bucket[arr[i] - min]++;  
	        }  
	        for(int i = 1; i < bucketCount; i++){  
	            bucket[i] = bucket[i] + bucket[i - 1];  
	        }  
	        //  
	        int [] copy = new int[arr.length];  
	        System.arraycopy(arr, 0, copy, 0, arr.length);  

	        //  
	        for(int i = arr.length - 1; i >= 0; i--){  
	            arr[--bucket[copy[i] - min]] = copy[i];  
	        }  
	        //  
	//      for(int i = 0; i <= arr.length - 1; i++){  
	//          arr[--bucket[copy[i] - min]] = copy[i];  
	//      }  
	    }  


		public static void main(String[] args) {
			int i;
			int a[] = { 8, 2, 3, 4, 3, 6, 6, 3, 9 };

			System.out.printf("before sort:");
			for (i = 0; i < a.length; i++)
				System.out.printf("%d ", a[i]);
			System.out.printf("\n");

	//		bucketSort(a, 10); // 
	//		bucketSort(a); // 
			bucketSort2(a); // 

			System.out.printf("after  sort:");
			for (i = 0; i < a.length; i++)
				System.out.printf("%d ", a[i]);
			System.out.printf("\n");
		}
	}

