			int[] array = { 8, 2, 3, 4, 3, 6, 6, 3, 9 };
			int[] array2 = { 2, 8, 5, 1, 10, 5, 9, 9, 3, 5, 6, 6, 2, 8, 2 };
			countingSort(array);
			countingSort2(array);
		}
