						} else if (rank[p + 1] > rank[q + 1]) {
							sa[k++] = q;
							j++;
						} else {
							if (rank12[p + 2] < rank12[q + 2]) {
								sa[k++] = p;
								i++;
							} else {
								sa[k++] = q;
								j++;
							}
						}
					}
				}
			}
			for (int m = i; m < sa12.length; m++) {
				sa[k++] = sa12[m];
			}
			for (int m = j; m < sa0.length; m++) {
				sa[k++] = sa0[m];
			}
			return sa;

		}

