			text = "DFDLKJLJldfasdlfjasdfkldjasfldafjdajfdsfjalkdsfaewefsdafdsfa#";
			pd = new PrefixDoubling();
			suffix = pd.solve(text);
			System.out.format("Text: %s%n", text);
			pd.report(suffix);

		}

